function limpar(arr) {
  return arr.filter(obj => Object.keys(obj).length > 0);
}

const plantios = limpar($('agrupa_plantio').all().map(i => i.json));
const colheitas = limpar($('agrupa_colheita').all().map(i => i.json));
const gastos = limpar($('agrupa_gasto').all().map(i => i.json));
const vendas = limpar($('agrupa_venda').all().map(i => i.json));

const resultado = {};

if (plantios.length > 0) {
  resultado.plantios = plantios;
}
if (colheitas.length > 0) {
  resultado.colheitas = colheitas;
}
if (gastos.length > 0) {
  resultado.gastos = gastos;
}
if (vendas.length > 0) {
  resultado.vendas = vendas;
}

return Object.keys(resultado).length > 0 ? [{ json: resultado }] : [];
