let dados = $("request_extrair_dados").first().json.dados;
const propriedade_id = $('busca_propriedade_relacionada').first().json.id || null;



const dadosComPropriedade = {
  ...dados,
  propriedade_id: propriedade_id
};

return {
  json: dadosComPropriedade
};

const dados = $("pega_dados_salvos").first().json.dados;

delete dados.propriedade_nome;
const chaveData = Object.keys(dados).find(chave => chave.startsWith("data_"));

if (chaveData && dados[chaveData]) {
  const [dia, mes, ano] = dados[chaveData].split('/');
  dados[chaveData] = `${ano}-${mes}-${dia}`;
}


return {
  json: {
    dados,
     resposta_usuario: {
      content: $('Webhook').first().json.body.json.message.text
    }
  }
};
