let outputString = items[0].json.output;

outputString = outputString.replace(/```json|```/g, '').trim();

let registros = [];

try {
  const parsed = JSON.parse(outputString);
  registros = Array.isArray(parsed) ? parsed : [parsed];
} catch (e) {
  return [{
    json: {
      erro: true,
      mensagem: "âŒ A resposta da IA estÃ¡ mal formatada ou ainda estÃ¡ como texto com mÃºltiplos JSONs colados."
    }
  }];
}

if (registros && typeof registros === 'object' && !Array.isArray(registros) && registros.erro) {
  return [{
    json: {
      erro: true,
      mensagem:  `${registros.erro}`
    }
  }];
}

const saida = [];

function formatarDataBRparaISO(dataTexto) {
  if (!dataTexto || typeof dataTexto !== 'string') return dataTexto;

  const partes = dataTexto.split('/');
  if (partes.length !== 3) return dataTexto;

  const [dia, mes, ano] = partes;
  return `${ano}-${mes.padStart(2, '0')}-${dia.padStart(2, '0')}`;
}

for (const item of registros) {
  const { tool, dados } = item;

  if (!tool || !dados) {
    saida.push({
      json: {
        erro: true,
        mensagem: "â— Registro sem 'tool' ou 'dados' encontrado.",
        itemOriginal: item
      }
    });
    continue;
  }

  const listaDados = Array.isArray(dados) ? dados : [dados];

  for (const d of listaDados) {
    const propriedade = d.propriedade_nome ?? null;
    const registradoPor = d.registrado_por;

    const comum = {
      ...(registradoPor && { registrado_por: registradoPor }),
      propriedade_nome: propriedade
    };

    switch (tool) {
      case "plantio":
        saida.push({
          json: {
            tabela: "plantio",
            cultura: d.cultura,
            data_plantio: formatarDataBRparaISO(d.data_plantio),
            area_plantada: d.area_plantada,
            ...comum
          }
        });
        break;

      case "colheita":
        saida.push({
          json: {
            tabela: "colheita",
            cultura: d.cultura,
            data_colheita: formatarDataBRparaISO(d.data_colheita),
            quantidade: d.quantidade,
            ...comum
          }
        });
        break;

      case "venda":
        saida.push({
          json: {
            tabela: "venda",
            cultura: d.cultura,
            data_venda: formatarDataBRparaISO(d.data_venda),
            quantidade: d.quantidade,
            valor_total: d.valor_total,
            ...comum
          }
        });
        break;

      case "gasto":
        saida.push({
          json: {
            tabela: "gasto",
            tipo_gasto: d.tipo_gasto,
            data_gasto: formatarDataBRparaISO(d.data_gasto),
            valor: d.valor,
            descricao: d.descricao && d.descricao.toLowerCase() !== "sem descriÃ§Ã£o"
              ? d.descricao
              : null,
            ...comum
          }
        });
        break;

      case "insumo":
        saida.push({
          json: {
            tabela: "insumo",
            nome_insumo: d.nome_insumo,
            tipo: d.tipo,
            quantidade: d.quantidade,
            preco_unitario: d.preco_unitario,
            ...comum
          }
        });
        break;

      case "trabalho":
        saida.push({
          json: {
            tabela: "trabalho",
            descricao: d.descricao,
            responsavel: d.responsavel,
            custo: d.custo ?? null,
            ...comum
          }
        });
        break;

      default:
        saida.push({
          json: {
            erro: true,
            mensagem: `â“ Tipo de registro desconhecido: '${tool}'`,
            itemOriginal: item
          }
        });
        break;
    }
  }
}

return saida;

