const dados = [];

const iaItems = $('extrai_dados_ia').all();

let propriedadesEncontradas = [];
try {
  propriedadesEncontradas = $('se_existir_propriedade_relacionada').all();
} catch (e) {
  propriedadesEncontradas = [];
}

let propIndex = 0;

for (const item of iaItems) {
  const original = item.json;

  if (original.erro) {
    dados.push(original);
    continue;
  }

  let propriedade_id = null;

  if (original.propriedade_nome && propriedadesEncontradas.length > 0) {
    propriedade_id = propriedadesEncontradas[propIndex]?.json?.id ?? null;
    propIndex++;
  }

  if ($('Webhook').first().json.body.registrado_por) {
    dados.push({
      ...original,
      propriedade_id,
      registrado_por: $('Webhook').first().json.body.registrado_por
    });
  } else {
    dados.push({
      ...original,
      propriedade_id,
      registrado_por: original.registrado_por ?? ''
    });
  }
}

return dados;
